jQuery(document).ready(function () {

    /****Category limit  *****/
    jQuery('.checkbox-wrap input[type="checkbox"]').change(function () {
        if (jQuery(this).attr('checked')) {
            jQuery(this).addClass('cselected');
        } else {
            jQuery(this).removeClass('cselected');
        }
        var ccount = jQuery('.checkbox-wrap input[type="checkbox"]:checked').length;
        if (ccount == 5) {
            jQuery('.checkbox-wrap input[type="checkbox"]').attr("disabled", "true");
            jQuery('.cselected').removeAttr('disabled');
        } else {
            jQuery('.checkbox-wrap input[type="checkbox"]').removeAttr("disabled");

        }
    });
    jQuery('.remodal-close').click(function () {
        var s = jQuery('#wpri-form-submit');
        s.get(0).reset();
        s.find('.fnm').remove();
        s.find('.f-remove').remove();
        s.find('#d-clean-url').html('');
        s.find('#d-startafire-url').html('');
        s.find('#d-short-url').html('');
        s.find('#d-short-canonical-url').html('');
        jQuery('#wpri-form-submit-messages').html('');
    });
    jQuery(".tabs-menu a").click(function (event) {
        event.preventDefault();
        jQuery(this).parent().addClass("current");
        jQuery(this).parent().siblings().removeClass("current");
        var tab = jQuery(this).attr("href");
        jQuery(".tab-content").not(tab).css("display", "none");
        jQuery(tab).fadeIn();
    });

    /* jQuery(".loop-listing-wrap").on("mouseover", function() {
          jQuery(this).find('.shareicon').show();
    }).on("mouseout", function() {
          jQuery(this).find('.shareicon').hide();
            	
    });*/
    jQuery(".share-link").click(function (e) {

        jQuery(".sharing-links").hide();
        if (jQuery(this).parents('.share-contr').find(".sharing-links").hasClass('sactive')) {
            //jQuery(this).parents('.share-contr').find(".sharing-links").hide();

            jQuery(this).parents('.share-contr').find(".sharing-links").removeClass('sactive');
        } else {
            jQuery(this).parents('.share-contr').find(".sharing-links").show();
            jQuery(this).parents('.share-contr').find(".sharing-links").addClass('sactive');
        }
        e.preventdefault();
    });

    jQuery(window).load(function () {
        jQuery('.riwp-vote-box').off('click');
        //  jQuery('.clickgo').on('click');
    });

    jQuery(".short-desc").click(function (e) {
        if (jQuery(this).parents('.submit-content').find('.excerpt').hasClass('dsactive')) {
            jQuery(this).parents('.submit-content').find('.excerpt').removeClass('dsactive');
            jQuery(this).parents('.submit-content').find('.excerpt').hide();
            jQuery(this).parents('.submit-content').find('.listing-social-count').removeAttr('style');
        } else {
            jQuery(this).parents('.submit-content').find('.excerpt').addClass('dsactive');
            jQuery(this).parents('.submit-content').find('.excerpt').show();
            jQuery(this).parents('.submit-content').find('.listing-social-count').attr('style', 'display:block !important');
        }

    });
    jQuery("#wpri-tweet-content").keyup(function (e) {
        var le = jQuery(this).val().length;
        if (le <= 140) {
            txt = jQuery(this).val();
        }
        le = 200 - le;

        if (le < 20) {
            jQuery(this).parent().find('.limit').addClass('chg');
        } else {
            jQuery(this).parent().find('.limit').removeClass('chg');
        }
        if (le >= 0) {
            jQuery('#t-count').html(le);
        }
        jQuery(this).val(txt);
    });
    jQuery("#wpri-share-content").keyup(function (e) {
        var le = jQuery(this).val().length;
        if (le <= 200) {
            txt = jQuery(this).val();
        }
        le = 200 - le;

        if (le < 20) {
            jQuery(this).parent().find('.limit').addClass('chg');
        } else {
            jQuery(this).parent().find('.limit').removeClass('chg');
        }
        if (le >= 0) {
            jQuery('#s-count').html(le);
        }
        jQuery(this).val(txt);
    });
    if (jQuery("#hcns-tweet-card").length == 0) {
        // alert('l');
    } else {
        var tweetId = jQuery("#hcns-tweet-card").attr('data-tweetid');
        console.log(tweetId);
        jQuery.getJSON("https://api.twitter.com/1/statuses/oembed.json?id=" + tweetId + "&align=center&callback=?",
            function (data) {
                jQuery('#hcns-tweet-card').html(data.html);
            });

    }

    ////////////
    jQuery(".hcns-tweet-card").each(function () {
        var tweetId = jQuery(this).attr('data-tweetid');
        var thisT = jQuery(this);
        if (tweetId != "") {
            jQuery.getJSON("https://api.twitter.com/1/statuses/oembed.json?id=" + tweetId + "&align=center&callback=?",
                function (data) {
                    thisT.html(data.html);
                });
        }
    });
    ////////////
    ////////////   
    jQuery(".related_artilces").mouseover(function () {
        jQuery('.r-active').hide().removeClass('r-active');
        jQuery(this).find('.ref-container').addClass('r-active');
        jQuery(this).find('.ref-container').show();


    });
    ////////////  
    jQuery(".related_artilces").mouseleave(function () {
        jQuery('.r-active').hide().removeClass('r-active');
        jQuery(this).find('.ref-container').hide();
    });
    //////////
    jQuery(".checkbox-wrap label").each(function (index) {
        //console.log( index + ": " + jQuery( this ).text() );
        var x = jQuery(this).text();
        if (x.charAt(1) == "#") {
            jQuery(this).remove();

        }

    });

});

jQuery(window).load(function () {
    jQuery('.short-desc').removeClass('dnone');
    jQuery('body').css('pointer-events', 'all'); //activate all pointer-events on body
});